import React from 'react';
import Button from '@material-ui/core/Button';
import axios from 'axios'



export default class Homepage extends React.Component {
    constructor(props) {
        super(props)
        this.state = {
            phrase: "Press a button to generate a phrase!",
            loading: false
        }
    }

    getFirstOrderPhrase = () => {
        this.setState({ loading: true })
        axios.get('https://frozen-lowlands-82583.herokuapp.com/firstOrderPhrase')
            .then(response => {
                const groomedResponse = response.data
                groomedResponse.charAt(0).toUpperCase() // capitalize first word in sentence

                this.setState({ phrase: groomedResponse, loading: false })
            })
            .catch(err => {
                this.getFirstOrderPhrase();
            })
    }

    getSecondOrderPhrase = () => {
        this.setState({ loading: true })
        axios.get('https://frozen-lowlands-82583.herokuapp.com/secondOrderPhrase')
            .then(response => {
                const groomedResponse = response.data
                groomedResponse.charAt(0).toUpperCase() // Capitalize first word in sentence
                console.log(groomedResponse.charAt(0))
                this.setState({ phrase: groomedResponse, loading: false })
            })
            .catch(error => {
                this.getSecondOrderPhrase()
            })
    }

    render() {
        return (
            <div style={styles.container}>
                <header style={styles.header}>
                    <h1>Text Generator</h1>
                    <h3 style={styles.subheader}>Generate random phrases from first and second order Markov Models</h3>
                </header>
                {
                    this.state.loading
                    ? <div style={styles.phraseContainer}><p>Loading...</p></div>
                    : <div style={styles.phraseContainer}>
                            {
                                this.state.phrase
                                ? <p>{this.state.phrase}</p>
                                : <p>Oops! Something went wrong, please try again!</p>
                            }
                        </div>
                }
                <div style={styles.buttonContainer}>
                    <Button variant="contained" color="primary" onClick={() => this.getFirstOrderPhrase()}>Try this</Button>
                    <Button variant="contained" color="primary" onClick={() => this.getSecondOrderPhrase()}>Or this</Button>
                </div>
            </div>
        )
    }
}

const styles = {
    container: {
        backgroundColor: '#203A43',
        width: '100vw',
        backgroundSize: 'cover',
        overflow: 'hidden',
        display: 'flex',
        flexDirection: 'column',
        alignItems: 'center',
        justifyContent: 'center',
        paddingBottom: 200
    },
    header: {
        fontFamily: 'Roboto',
        color: 'white',
        width: '80%',
        backgroundColor: '#2C5364',
        boxShadow: '0 3px 6px rgba(0,0,0,0.16), 0 3px 6px rgba(0,0,0,0.23)',
        padding: 20,
        margin: 5,
        display: 'flex',
        flexDirection: 'column',
        alignItems: 'center'
    },
    buttonContainer: {
        width: '90%',
        display: 'flex',
        flexDirection: 'row',
        flexWrap: 'wrap',
        justifyContent: 'space-around'
    },
    phraseContainer: {
        fontFamily: 'Arial',
        color: 'white',
        fontSize: 20,
        width: '80vw',
        height: '200px',
        padding: 20,
        margin: 20,
        backgroundColor: 'rgba(255, 255, 255, .1)',
        boxShadow: '0 3px 6px rgba(0,0,0,0.16), 0 3px 6px rgba(0,0,0,0.23)',
        display: 'flex',
        flexDirection: 'column',
        borderRadius: 10,
        alignItems: 'center',
        justifyContent: 'center'

    },
    subheader: {
        color: '#cecece'
    },
    subtext: {
        fontSize: 12,
        color: '#cecece'
    },
    link: {
        color: "white",
        fontSize: 13,
        textDecoration: 'none'
    },
    icon: {
        marginRight: 10,
    },
    linkContainer: {
        width: '50%',
        padding: 20,
        backgroundColor: 'rgba(255, 255, 255, .1)',
        boxShadow: '0 3px 6px rgba(0,0,0,0.16), 0 3px 6px rgba(0,0,0,0.23)',
    },
    image: {
        maxWidth: 200,
        boxShadow: '0 3px 6px rgba(0,0,0,0.16), 0 3px 6px rgba(0,0,0,0.23)',
        margin: 10
    }
}
